var mongoose = require('mongoose');
var Schema = mongoose.Schema;

// making schemas for inserting data in mongo
var users_schema = Schema({
    first_name: String ,
    last_name: String ,
    username: String,
    password: String,
    email: String,
    role: Number,
    favorite:Array,
    permissions:Array
});
var phone_schema = Schema({
    name:String,
    departmenet : String,
    inside_phone_number:String,
    phone_number:String,
    email:String,
    room_number:String

});
logs_schema = Schema({
    log : String,
    type:String
});
permissions = Schema({
    title:String,
    name:String
});


module.exports = (function () {
    var _return = {};
    _return.users = mongoose.model('users', users_schema);
    _return.logs = mongoose.model('log', logs_schema);
    _return.phones = mongoose.model('phone_schema', phone_schema);
    _return.permissions = mongoose.model('permissions', permissions);
    return _return;
})();
